import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="login">
                <Card >
                    <Menu mode="horizontal">
                        <Menu.Item>Login</Menu.Item>
                    </Menu>
                    <div >
                        {/*<ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed}/>*/}
                        {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials or something is wrong</div>}
                        {this.state.showSuccessMessage && <div>Login Sucessful</div>}
                        {/*<ShowLoginSuccessMessage showSuccessMessage={this.state.showSuccessMessage}/>*/}
                        <div className="login-input-row">
                            <Input placeholder="User Name" name="username" value={this.state.username} onChange={this.handleChange} />
                        </div>
                        <div className="login-input-row">
                            <Input.Password placeholder="Password" name="password" value={this.state.password} onChange={this.handleChange} />
                        </div>
                        <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                    </div>
                </Card>
    </div>
  );
}

export default App;
